import { useReducer } from "react";
import CartContext from "./cart-ctx";

const initCartState = { items: [], totalAmount: 0 };

const cartReducer = (state, action) => {
  if (action.type === "ADD_ITEM") {
    const updatedTotalAmount =
      state.totalAmount + action.item.quantity * action.item.price;
    
    const existingItemIndex = state.items.findIndex(
      (item) => item.id === action.id
    );
    const existingItem = state.items[existingItemIndex];

    
    let updatedItems; 
    if (existingItem) {
      const updatedItem = { ...existingItem, quantity: existingItem.quantity + action.item.quantity };
      
        updatedItems = [...state.items];
        updatedItems[existingItemIndex] = updatedItem;
    } else {
      updatedItems = state.items.concat(action.item);
    }

    return {
      items: updatedItems,
      totalAmount: updatedTotalAmount,
    };
  }
  if (action.type === "REMOVE_ITEM") {
    if (state.items.length === 1) {
      const updatedItems = state.items.slice();
      return {
        items: [],
        totalAmount: 0,
      };
    } else {
      const updatedItems = state.items.filter((id) => id !== action.itemId);

      const itemIndex = state.items.findIndex((id) => id === action.itemId);
      const updatedTotalAmount = state.totalAmount - state.items[itemIndex];

      return {
        items: updatedItems,
        totalAmount: updatedTotalAmount,
      };
    }
  }
  return initCartState;
};

const CartProvider = (props) => {
  const [cartState, dispatchCartState] = useReducer(cartReducer, initCartState);

  const addItemHandler = (item) => {
    dispatchCartState({ type: "ADD_ITEM", item: item });
  };

  const removeItemHandler = (itemId) => {
    dispatchCartState({ type: "REMOVE_ITEM", itemId });
  };

  const cartContext = {
    items: cartState.items,
    totalAmount: cartState.totalAmount,
    addItem: addItemHandler,
    removeItem: removeItemHandler,
  };

  return (
    <CartContext.Provider value={cartContext}>
      {props.children}
    </CartContext.Provider>
  );
};

export default CartProvider;
