  }

  return (
    <form className={classes.form}>
      <Input  
        label="Quantity"  
        input={{ id: "quantity_" + props.id, defaultValue: "1", min: "1" }}
      />
      <button onClick={onAddItem}>+ Add</button>
    </form>
  );
};

