import React, { useReducer } from "react";

export const AuthContext = React.createContext({
  loginDetail: {},
  isLogin: false,
  addLogin: () => {},
});

const initAuthState = {
  email: "",
  password: "",
};

const authReducer = (state, action) => {
  switch (action.type) {
    case "LOGIN":
      return { ...state.loginDetail, isLogin: true };
    default:
      throw new Error("Something went wrong!");
  }
};

const AuthProvider = (props) => {
  const [authState, dispatchAuth] = useReducer(authReducer, initAuthState);

  const addLoginHandler = (loginInfo) => {
    dispatchAuth({ type: "LOGIN", login: loginInfo });
  };

  const authContext = {
    loginDetail: authState.loginDetail,
    isLogin: authState.isLogin,
    addLogin: addLoginHandler,
  };
  return (
    <AuthContext.Provider value={authContext}>
      {props.children}
    </AuthContext.Provider>
  );
};

export default AuthProvider;
