import React, { useReducer } from "react";

export const AuthContext = React.createContext({
    isAuth: false,
    authIsValid: false
})

const initAuthState = {
    isLogin: false,
    authIsValid: false,
}

const authReducer = (state, action) => {
    switch (action.type) {
        case 'AUTH':
            return {isLogin: true, authIsValid: false}
        case 'VALID':
            return {isLogin: false, authIsValid: true}
        default:
            throw new Error('Error!')
    }
}

const AuthProvider = (props) => {
    const [authState, dispatchAuth] = useReducer(authReducer, initAuthState);

    const authHandler = () => {
        dispatchAuth({type: 'AUTH'})
    }

    const authContext = {
        isLogin: authHandler,
      authIsValid: authState.authIsValid,
    };

    return <AuthContext.Provider value={authContext}>
        {props.children}
    </AuthContext.Provider>
};

export default AuthProvider;
