import React, { Fragment, useContext, useState } from "react";

import CartButton from "./CartButton";

import classes from "./Header.module.css";
import mealTable from "../../assets/meal.png";
import AuthContext from "../../store/auth-ctx";

const Header = (props) => {
  const [scroll, setScroll] = useState(false);
  const authCtx = useContext(AuthContext);

  const changeBackground = () => {
    if (window.scrollY > 5) {
      setScroll(true);
    } else {
      setScroll(false);
    }
  };

  window.addEventListener("scroll", changeBackground);

  const isLogin = authCtx.isLogin;

  return (
    <Fragment>
      <header
        className={
          scroll
            ? `${classes.header} ${classes["scroll-header"]}`
            : classes.header
        }
      >
        <a href="#/" className={classes.logo}>
          <img src="#" alt="logo" />
          <h1>forks and fingers</h1>
        </a>
        <div className={classes.links}>
          <a href="#/">Blogs</a>
          {!isLogin && <a href="#/">Login</a>}
          {!isLogin && <a href="#/">Sign Up</a>}
          <CartButton onShowCart={props.onShowCart} />
        </div>
      </header>
      <div className={classes["main-image"]}>
        <img src={mealTable} alt="meal-table" />
      </div>
    </Fragment>
  );
};

export default Header;
