import React, {useReducer} from "react";

export const AuthContext = React.createContext({
  email: "",
  password: "",
  addEmail: () => {},
  addPassword: () => {},
});

const initAuthState = {
  email: "",
  password: "",
};

const authReducer = (state, action) => {
  switch (action.type) {
    case "EMAIL":
      return { email: state.email, password: "" };
    case "PASSWORD":
      return { email: "", password: state.password };
    default:
      throw new Error("Something went wrong!");
  }
};

const AuthProvider = (props) => {
  const [authState, dispatchAuth] = useReducer(authReducer, initAuthState);

  const addEmailHandler = (email) => {
    dispatchAuth({ type: "EMAIL", email });
  };

  const addPasswordHandler = (password) => {
    dispatchAuth({ type: "PASSWORD", password });
  };

  const authContext = {
    email: authState.email,
    password: authState.password,
    addEmail: addEmailHandler,
    addPassword: addPasswordHandler,
  };
  return (
    <AuthContext.Provider value={authContext}>
      {props.children}
    </AuthContext.Provider>
  );
};

export default AuthProvider;
