import { Fragment, useReducer } from "react";

import Cart from "./components/Cart/Cart";
import Header from "./components/Layout/Header";
import Meals from "./meals/Meals";

const initCartState = { closeCart: false, showCart: false };

const cartReducer = (cartState, action) => {
  switch (action.type) {
    case "CLOSE":
      return { ...cartState, closeCart: true };
    case "SHOW":
      return { ...cartState, showCart: true };
    default:
      throw new Error('ERROR!')
  }
};

function App() {
  const [state, dispatch] = useReducer(cartReducer, initCartState);
  // const [close, setClose] = useState(false);
  // const [showCart, setShowCart] = useState(false);

  const closeCartHandler = () => {
    dispatch("CLOSE");
  };

  const showCartHandler = () => {
    dispatch("SHOW");
  };

  return (
    <Fragment>
      {!state.closeCart && <Cart onClose={closeCartHandler} />}
      <Header onShowCart={showCartHandler} />
      <main>
        <Meals />
      </main>
    </Fragment>
  );
}

export default App;
